const StepOne = ({ nextStep, setSelectedCategory }) => {
  const [error, setError] = useState("");
  const [selectedCategoryId, setSelectedCategoryId] = useState(null);

  const handleCategoryClick = (category: {
    id: any;
    icon?: JSX.Element;
    title?: string;
  }) => {
    setSelectedCategoryId(category.id);
    setSelectedCategory(category);
    setError("");
  };

  const handleNextClick = () => {
    if (!selectedCategoryId) {
      setError("يجب اختيار قسم للانتقال إلى الخطوة التالية.");
    } else {
      nextStep();
    }
  };

  return (
    <div className="flex flex-col gap-2">
      <div className="mb-4 grid grid-cols-2 gap-4 text-white lg:grid-cols-3">
        {categories.map(category => (
          <div
            key={category.id}
            onClick={() => handleCategoryClick(category)}
            className={`flex cursor-pointer flex-col items-center justify-center gap-4 rounded-md border bg-background p-6 ${
              selectedCategoryId === category.id
                ? "border-secondary-normal"
                : "border-background"
            }`}
          >
            <div className="text-4xl">{category.icon}</div>
            <div className="lg:p-6">
              <p className="text-lg">{category.title}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="flex flex-col gap-2">
        {error ? (
          <p className="text-center text-error">{error}</p>
        ) : (
          <p className="opacity-0">
            لا توجد مشاكل، يمكنك الذهاب للخطوة التالية
          </p>
        )}
        <Cta text="التالي" onClick={handleNextClick} type="button" />
      </div>
    </div>
  );
};

export default StepOne;
